       tags:  J.select('meta[property="og:novel:category"]').attr('content')+','+ J.select('meta[property="og:novel:status"]').attr('content'),
       latest: J.select('meta[property="og:novel:latest_chapter_name"]').attr('content') + '(' + J.select('meta[property="og:novel:update_time"]').attr('content')+')',
       intro: J.select('meta[property="og:description"]').attr('content'),
       cover: String(detail).replace(/(\d+)_(\d+).*/,'uploads/$1/$2.jpg'),
       catalogUrl: detail
    }
});
</js>",
  "bookUrl": "catalogUrl",
